    filename = url.split("/")[-1]
    print("Downloading:", filename)

    r = requests.get(url)

    if r.status_code == 200:
        with open(f"Locations pdfs/DIEGO MARTIN WEST/{filename}", "wb") as f:
            f.write(r.content)

