urls = re.findall(r'https://[^"]+\.PDF', html)

# Create folder
os.makedirs("DIEGO MARTIN NORTH EAST", exist_ok=True)

for url in urls:
    filename = url.split("/")[-1]
    print("Downloading:", filename)

    r = requests.get(url)

    if r.status_code == 200:
        with open(f"DIEGO MARTIN NORTH EAST/{filename}", "wb") as f:
            f.write(r.content)

print("Done.")
